import { 
  Heading,
  Text,
 } from '@chakra-ui/react';
import {React, useEffect, useState} from 'react'

const Dashboard = () => {

  const [userDetails, setUserDetails] = useState("");

  useEffect(() => {
    async function getUsername() {
      const req = await fetch("http://localhost:8000/auth/users/me", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Token ${sessionStorage.getItem("auth_token")}`,
        }
      })

      if(req.ok)  {
        const data = await req.json()
        setUserDetails(data)
      }
      else  {
        const errorDetails = await req.json()
        console.error("There was an error! ", errorDetails)
      }

    }

    getUsername();

    return () => {}

  }, [])

  return (
    <Container>
      <Heading>Hello {userDetails["username"]}</Heading>
      <Text>Lets get some work done ✏️</Text>
    </Container>
  )
}

export default Dashboard;
