import { 
  Container,
  Heading,
  Text,
  Tag,
  VStack,
  Flex,
  Spacer,
  Button,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  FormControl,
  FormLabel,
  Input,
  Textarea,
  Checkbox,
 } from '@chakra-ui/react';
import {React, useEffect, useState} from 'react'

const Dashboard = () => {

  const [userDetails, setUserDetails] = useState({});
  const [loading, setLoading] = useState(false);
  const { isOpen, onOpen, onClose } = useDisclosure()

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [todoItems, setTodoItems] = useState([]);

  useEffect(() => {
    async function fetchData() {

      const urls = [fetch("http://localhost:8000/auth/users/me", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Token ${sessionStorage.getItem("auth_token")}`
        }
      }), fetch("http://localhost:8000/api/todos/", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Token ${sessionStorage.getItem("auth_token")}`
        }
      })]

      const req = await Promise.all(urls)
      const [userDetailsValues, userTodosValues] = req



      const userDetailsRes = await userDetailsValues.json()
      const userTodosRes = await userTodosValues.json()

      setUserDetails(userDetailsRes);
      setTodoItems(userTodosRes);

    }

    fetchData();

    return () => {}

  }, [])


  async function handleFormSubmition() {
    setLoading(true)
    const reqBody = {
      "title": title,
      "description": description,
    }

    const request = await fetch("http://localhost:8000/api/todos/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Token ${sessionStorage.getItem("auth_token")}`
      },
      body: JSON.stringify(reqBody)
    })

    if(!request.ok) {
      const errorData = await request.json();
      console.log("Error Creating User: ", errorData)
      setLoading(false);

    }
    else  {
      const response = await request.json();
      setTodoItems((todoItems) => todoItems.concat([ ...[response]]))
    }

    setLoading(false);
    onClose();
  }

  const handleTaskChange = (curr) => {
    setTodoItems(() => todoItems.map((val, i) => {
      if(val.title == curr.title) {
        return {...val, status: !val.status}
      }
      return val
    }))
  }

  return (
    <Container height={"40px"}>
      <Flex color="white" width={"100%"}>
      <Heading>Hello {userDetails.username}</Heading>
      <Text>Lets get some work done ✏️</Text>
          <Spacer />
          <Button colorScheme="blue" onClick={onOpen}>+</Button>
          <Modal onClose={onClose} isOpen={isOpen} isCentered>
            <ModalOverlay />
            <ModalContent>
              <ModalHeader>Add a task!</ModalHeader>
              <ModalCloseButton />
              <ModalBody>
                <FormControl id="title" isRequired>
                  <FormLabel>Title</FormLabel>
                  <Input type='text' onChange={(e) => setTitle(e.target.value)}/>
                </FormControl>
                <FormControl id="Description">
                  <FormLabel>Description: </FormLabel>
                  <Textarea size="sm" placeholder='What is the task about' onChange={(e) => {setDescription(e.target.value)}} />
                </FormControl>
              </ModalBody>
              <ModalFooter>
                <Button colorScheme='teal' onClick={handleFormSubmition} isLoading={loading} loadingText={"Adding"}>
                  Add
                </Button>
              </ModalFooter>
            </ModalContent>
          </Modal>
        </Flex>
      <VStack mt={10} alignItems="flex-start" spacing={3}>
        <Flex color="white" width={"100%"}>
          <Tag size="lg" colorScheme="blue">TODO</Tag>
          <Spacer />
          <Button colorScheme="blue" onClick={onOpen}>+</Button>
          <Modal onClose={onClose} isOpen={isOpen} isCentered>
            <ModalOverlay />
            <ModalContent>
              <ModalHeader>Add a task!</ModalHeader>
              <ModalCloseButton />
              <ModalBody>
                <FormControl id="title" isRequired>
                  <FormLabel>Title</FormLabel>
                  <Input type='text' onChange={(e) => setTitle(e.target.value)}/>
                </FormControl>
                <FormControl id="Description">
                  <FormLabel>Description: </FormLabel>
                  <Textarea size="sm" placeholder='What is the task about' onChange={(e) => {setDescription(e.target.value)}} />
                </FormControl>
              </ModalBody>
              <ModalFooter>
                <Button colorScheme='teal' onClick={handleFormSubmition} isLoading={loading} loadingText={"Adding"}>
                  Add
                </Button>
              </ModalFooter>
            </ModalContent>
          </Modal>
        </Flex>
        {todoItems.map((curr, i) => {
          if(curr.status == false) {
            return (
              <Checkbox key={curr.title} onChange={() => handleTaskChange(curr)}>{curr.title}</Checkbox>
            )
          } 
        })}
        <Flex color="white" width={"100%"} mt={5}>
          <Tag size="lg" colorScheme="green">COMPLETED</Tag>
        </Flex>
        {todoItems.map((curr, i) => {
          if(curr.status == true) {
            return (
              <Checkbox key={curr.title} onChange={() => handleTaskChange(curr)} isChecked>{curr.title}</Checkbox>
            )
          } 
        })}
      </VStack>
    </Container>
  )
}

export default Dashboard;
