import { 
  Container,
  Heading,
  Text,
  Tag,
  VStack,
  Flex,
  Spacer,
  Button,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  FormControl,
  FormLabel,
  Input,
  Textarea,
 } from '@chakra-ui/react';
import {React, useEffect, useState} from 'react'

const Dashboard = () => {

  const [userDetails, setUserDetails] = useState("");
  const [loading, setLoading] = useState(false);
  const { isOpen, onOpen, onClose } = useDisclosure()

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    async function getUsername() {
      const req = await fetch("http://localhost:8000/auth/users/me", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Token ${sessionStorage.getItem("auth_token")}`,
        }
      })

      if(req.ok)  {
        const data = await req.json()
        console.log(data)
        setUserDetails(data)
      }
      else  {
        const errorDetails = await req.json()
        console.error("There was an error! ", errorDetails)
      }

    }

    getUsername();

    return () => {}

  }, [])


  async function handleFormSubmition() {
    setLoading(true)
    const reqBody = {
      "title": title,
      "description": description,
    }

    const request = await fetch("http://localhost:8000/api/add/", {
      method: "POST",

    })

    setLoading(false);
    onClose();
  }

  return (
    <Container height={"40px"}>
      <Heading>Hello {userDetails["username"]}</Heading>
      <Text>Lets get some work done ✏️</Text>
      <VStack mt={10}>
        <Flex color="white" width={"100%"}>
          <Tag size="lg" colorScheme="blue">TODO</Tag>
          <Spacer />
          <Button colorScheme="blue" onClick={onOpen}>+</Button>
          <Modal onClose={onClose} isOpen={isOpen} isCentered>
            <ModalOverlay />
            <ModalContent>
              <ModalHeader>Add a task!</ModalHeader>
              <ModalCloseButton />
              <ModalBody>
                <FormControl id="title" isRequired>
                  <FormLabel>Title</FormLabel>
                  <Input type='text' onChange={(e) => setTitle(e.target.value)}/>
                </FormControl>
                <FormControl id="Description">
                  <FormLabel>Description: </FormLabel>
                  <Textarea size="sm" placeholder='What is the task about' onChange={(e) => {setDescription(e.target.value)}} />
                </FormControl>
              </ModalBody>
              <ModalFooter>
                <Button colorScheme='teal' onClick={handleFormSubmition} isLoading={loading} loadingText={"Adding"}>
                  Add
                </Button>
              </ModalFooter>
            </ModalContent>
          </Modal>
        </Flex>
      </VStack>
    </Container>
  )
}

export default Dashboard;
