import { 
  Container,
  Heading,
  Text,
  Tag,
  Divider,
  VStack,
  Flex,
  Spacer,
  Button,
 } from '@chakra-ui/react';
import {React, useEffect, useState} from 'react'

const Dashboard = () => {

  const [userDetails, setUserDetails] = useState("");

  useEffect(() => {
    async function getUsername() {
      const req = await fetch("http://localhost:8000/auth/users/me", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Token ${sessionStorage.getItem("auth_token")}`,
        }
      })

      if(req.ok)  {
        const data = await req.json()
        console.log(data)
        setUserDetails(data)
      }
      else  {
        const errorDetails = await req.json()
        console.error("There was an error! ", errorDetails)
      }

    }

    getUsername();

    return () => {}

  }, [])

  return (
    <Container height={"40px"}>
      <Heading>Hello Ishan</Heading>
      <Text>Lets get some work done ✏️</Text>
      <Divider orientation='vertical' />
      <VStack>
        <Flex color="white" width={"100vw"}>
          <Tag size="lg" colorScheme="blue">TODO</Tag>
          <Spacer />
          <Button colorScheme="blue">+</Button>
        </Flex>
      </VStack>
    </Container>
  )
}

export default Dashboard;
